import React from 'react'
import { useState } from 'react'

const QuestionToAI = () => {
    const [userInput, setUserInput] = useState({
        'user_input': '',
    });
    const [aiOutput, setAiOutput] = useState();

    const sendingQuestion = async() => {
        const response = await fetch('http://127.0.0.1:8000/users/question', {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(userInput),
        })
    }

    const handleChangeUserInput = (e) => {
        setUserInput({
            'user_input': e.target.value
        });
    }

    return (
        <>
            <div className='question-to-ai'>
                <form className='question-form' onSubmit={sendingQuestion}>
                    <input className='question-box' type='text' placeholder='質問を入力' onChange={handleChangeUserInput} value={userInput.user_input} />
                    <button className='send-button' type='submit'>回答</button>
                </form>
                <textarea value={userInput.user_input} onChange={handleChangeUserInput}></textarea>
                <button onClick={sendingQuestion}>回答</button>
                <p>{aiOutput}a</p>
            </div>
        </>
    )
}

export default QuestionToAI
