import React, { useEffect, useState } from 'react';
import eyeOpen from '../asset/eye-open.png'
import eyeClose from '../asset/eye-close.png'

const Login = () => {
    const [showPassword, setShowPassword] = useState(() => 
        JSON.parse(localStorage.getItem('showPassword')) || true);
    useEffect(() => {
        localStorage.setItem('showPassword', JSON.stringify(showPassword));
    },[showPassword]);
    return (
        <>
            <div className='login-form'>
                <form>
                    <div className='mail'>
                        <label htmlFor="email">メールアドレス</label>
                        <input type="email" id='email' name='email' />
                    </div>
                    <div className='password'>
                        <label htmlFor="password">パスワード</label>
                        <input type={showPassword ? 'password' : 'text'} id='password' name='password' />
                    </div>
                    <a>パスワードをお忘れの方</a>
                    <input className='submit-btn' type="submit" value="ログイン" />
                </form>
                <img className='eye-img' src={showPassword ? eyeClose : eyeOpen} 
                    onClick={() => showPassword ? setShowPassword(false) : setShowPassword(true)} 
                    alt="eye-image" />
            </div>
        </>
    )
}

export default Login
